import React from "react";
import { useSelector } from "react-redux";
import { Link, Redirect } from "react-router-dom";
import { isEmpty } from "lodash";

const UserProfile = () => {
    const user = useSelector(state => state.user);

    if (isEmpty(user)) return <Redirect to="/" />;

    return (
        <div className="user-account">
            <div className="row">
                <div className="col-md-3 col-sm-4 col-xs-12">
                    <aside>
                        <div className="avatar-layer">
                            <div className="img-layer">
                                <a href="" className="change-image">
                                    <i className="zmdi zmdi-edit"></i>
                                </a>
                                <img src="images/pic/avatar.jpg" />
                            </div>
                            <div className="detail">
                                <span>{user.fullname}</span>
                                <span> تاریخ عضویت :  {user.createdAt} </span>
                            </div>
                        </div>

                        <section>
                            <header>
                                <h3> میز کار </h3>
                            </header>
                            <div className="inner">
                                <ul>
                                    <li>
                                        <Link to="/logout">
                                            {" "}
                                            خروج از حساب کاربری{" "}
                                        </Link>
                                    </li>
                                </ul>
                            </div>
                        </section>
                    </aside>
                </div>
                <div className="col-md-9 col-sm-8 col-xs-12">
                    <section className="user-account-content">
                        <header>
                            <h1> داشبورد </h1>
                        </header>
                        <div className="inner">
                            <div className="account-information">
                                <h3> اطلاعات کاربری </h3>
                                <ul>
                                    <li>
                                        {" "}
                                        <i className="zmdi zmdi-account"></i>{" "}
                                        نام و نام خانوادگی : {user.fullname}{" "}
                                    </li>
                                    <li>
                                        {" "}
                                        <i className="zmdi zmdi-assignment-account"></i>{" "}
                                        نام کاربری : younes.gh{" "}
                                    </li>
                                    <li>
                                        {" "}
                                        <i className="zmdi zmdi-email"></i>{" "}
                                        ایمیل : {user.email}{" "}
                                    </li>
                                    <li>
                                        {" "}
                                        <i className="zmdi zmdi-calendar-check"></i>{" "}
                                        تاریخ عضویت : 01/01/1395{" "}
                                    </li>
                                    <li>
                                        {" "}
                                        <i className="zmdi zmdi-smartphone-android"></i>{" "}
                                        شماره تماس : 0912000000{" "}
                                    </li>
                                </ul>
                            </div>
                        </div>
                    </section>
                </div>
            </div>
        </div>
    );
};

export default UserProfile;
